import React, {useEffect, useState} from 'react';
import './App.css';
import BasicTextFields from './InputBox'
import ButtonAppBar from "./appbar";
import Box from "@material-ui/core/Box";
import {makeStyles, createStyles} from '@material-ui/core/styles';
import Button from "@material-ui/core/Button";
import RadioButtonsGroup from "./radiobuttons";



import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';
















const useStyles = makeStyles((theme) => createStyles({
    root: {
        // backgroundColor: "#ff1133",
        border: "2px solid blue",
        backgroundColor: "#ffffff",
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginLeft: "22%",
        shadowBlur: "#2224FF",
        marginTop: "5%",
        marginBottom: "5%",
        marginRight: "22%",
        borderRadius: 20,
        padding: 30,
    },
}));

function App() {

    const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

  const [open, setOpen] = React.useState(false);

  const handleClose = () => {
    setOpen(false);
  };


    const classes = useStyles();
    const [data, setData] = useState([]);
    const [value, setValue] = useState([]);

    const state = data.reduce(function (map, obj) {
        map[obj] = "";
        return map;
    }, {});

    async function getsymtoms(key) {
        state[key.target.id] = key.target.value;
        console.log(state);
    }

    async function getGender(key)
    {
        state["Gender"] = key.target.value;
    }


     // GET request using fetch inside useEffect React hook
    async function getcolnames() {
        const response = await fetch('http://127.0.0.1:8000/symptoms/',
            {

                method: "GET",
                headers: {
                    'Content-Type': 'application/json'
                },
            });
        const json = await response.json();
        setData(json['symptoms']);
        console.log(json['symptoms'])

    }


    useEffect(() => {
        // GET request using fetch inside useEffect React hook
        getcolnames()
    }, []);


    // POST request
    async function posttoapi() {
        console.log(state);
        const response = await fetch('http://127.0.0.1:8000/predictdisease/',
            {

                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    "symptoms": state
                })
            }).then(response => response.json())
            .catch(error => error);
        var obj = JSON.parse(response);
        setValue(obj["messege"]);
        setOpen(true);
        alert(obj["messege"]);
        console.log(obj["messege"])
    }


    async function validation()
    {
        for (const [key, input] of Object.entries(state))
        {
            if(input == ""){
                alert(key + " should not be empty and should be numeric")
                return 0;
            }
        }
        posttoapi();

    }


    return (
        <div className="App">

            <div>
                <ButtonAppBar/>
            </div>
            <Box display="flex" className={classes.root} flexWrap="wrap">
                <RadioButtonsGroup clicked={getGender}/>
                <BasicTextFields label={data} getdata={getsymtoms}/>
                <Button variant="contained" color="primary" onClick ={validation}>SUBMIT</Button>
            </Box>
            <div>
      <Dialog
        open={open}
        TransitionComponent={Transition}
        keepMounted
        onClose={handleClose}
        aria-labelledby="alert-dialog-slide-title"
        aria-describedby="alert-dialog-slide-description"
      >
        <DialogTitle id="alert-dialog-slide-title">{"Use Google's location service?"}</DialogTitle>
        <DialogContent>
          <DialogContentText id="alert-dialog-slide-description">
            Let Google help apps determine location. This means sending anonymous location data to
            Google, even when no apps are running.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            okay
          </Button>
        </DialogActions>
      </Dialog>
    </div>


        </div>

    );
}

export default App;
