import React, {useEffect, useMemo, useState} from 'react';
import './App.css';
import BasicTextFields from './InputBox'
import ButtonAppBar from "./appbar";
import Box from "@material-ui/core/Box";
import {makeStyles, createStyles} from '@material-ui/core/styles';
import Button from "@material-ui/core/Button";
import RadioButtonsGroup from "./radiobuttons";
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import Slide from '@material-ui/core/Slide';

const useStyles = makeStyles((theme) => createStyles({
    root: {
        // backgroundColor: "#ff1133",
        border: "2px solid blue",
        backgroundColor: "#ffffff",
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginLeft: "22%",
        shadowBlur: "#2224FF",
        marginTop: "5%",
        marginBottom: "5%",
        marginRight: "22%",
        borderRadius: 20,
        padding: 30,
    },
}));

function App() {

    const Transition = React.forwardRef(function Transition(props, ref) {
        return <Slide direction="up" ref={ref} {...props} />;
    });

    const [open, setOpen] = React.useState(false);


    const handleClose = () => {
        setOpen(false);
    };


    const classes = useStyles();
    const [data, setData] = useState([]);
    const [value, setValue] = useState([]);
    const [er, setError] = useState({});
    const [formState, setFormState] = useState({});
    const [disabled, setDisable] = useState(true);

    function getsymtoms(key) {
        const newErrors = {...er};
        const newFormState = {...formState};
        const decimal = /^[-+]?[0-9]+\.[0-9]+$/;
        const numbers = /^[0-9]+$/;
        if(key.target.value.match(numbers) || key.target.value.match(decimal))
        {
            newErrors[key.target.id] = false;
        }
        else
        {
            newErrors[key.target.id] = true;
        }
        newFormState[key.target.id] = key.target.value;
        setFormState(newFormState);
        setError(newErrors);
        //console.log("updating ---" + JSON.stringify(formState));

    }




    async function getGender(key) {
        const newFormState = {...formState};
        newFormState["Gender"] = key.target.value;
        setFormState(newFormState);
    }


    // GET request using fetch inside useEffect React hook
    async function getcolnames() {
        const response = await fetch('http://127.0.0.1:8000/symptoms/',
            {

                method: "GET",
                headers: {
                    'Content-Type': 'application/json'
                },
            });
        const json = await response.json();
        setData(json['symptoms']);
        const formData = json['symptoms'].reduce(function (map, obj) {
            map[obj] = "";
            return map;
        }, {});
        setFormState(formData)
    }


    useEffect(() => {
        // GET request using fetch inside useEffect React hook
        getcolnames();

    }, []);


    // POST request
    async function posttoapi() {


        const response = await fetch('http://127.0.0.1:8000/predictdisease/',
            {

                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    "symptoms": formState
                })
            }).then(response => response.json())
            .catch(error => error);
        var obj = JSON.parse(response);
        setValue(obj["messege"]);
        setOpen(true);
    }

    async function handleChangeFile(e)
    {
        const file = e.target.files[0];
        let formData = new FormData();
        formData.append('file', file);
        console.log(formData)
        //Make a request to server and send formData
        // }
    }

    async function validation() {
        const newErrors = {...er};
        const numbers = /^[0-9]+$/;
        const decimal = /^[-+]?[0-9]+\.[0-9]+$/
        for (const [key, input] of Object.entries(er)) {
            if(input == "")
            {
                //alert(key + " should not be empty");
                newErrors[key.target.id] = true;

            }
            else if (!input.match(numbers) && !input.match(decimal))
            {
                newErrors[key.target.id] = true;
            }

        }
        setError(newErrors);
    }

    return (
        <div className="App">

            <div>
                <ButtonAppBar/>
            </div>
            <Box display="flex" className={classes.root} flexWrap="wrap">
                <RadioButtonsGroup clicked={getGender}/>
                <BasicTextFields label={data} getdata={getsymtoms} error={er}/>

                <Button variant="contained" color="primary" onClick={validation} disabled={disabled}>SUBMIT</Button>
                <input type="file" onChange={handleChangeFile}/>
            </Box>
            <div>
                <Dialog
                    open={open}
                    TransitionComponent={Transition}
                    keepMounted
                    onClose={handleClose}
                    aria-labelledby="alert-dialog-slide-title"
                    aria-describedby="alert-dialog-slide-description"
                >
                    <DialogTitle id="alert-dialog-slide-title">{"THANKS FOR USING OUR SITE"}</DialogTitle>
                    <DialogContent>
                        <DialogContentText id="alert-dialog-slide-description">
                            {value}
                        </DialogContentText>
                    </DialogContent>
                    <DialogActions>
                        <Button onClick={handleClose} color="primary">
                            okay
                        </Button>
                    </DialogActions>
                </Dialog>
            </div>
        </div>

    );
}

export default App;
