import React, {useState} from 'react';
import IconLabelButtons from "./uploadicon";
import CSVReader from "react-csv-reader";
import Box from "@material-ui/core/Box";
import {createStyles, makeStyles} from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";


const useStyles = makeStyles((theme) => createStyles({
    head: {
        border: "2px solid blue",
        min: "10ch",
        backgroundColor: "#ffffff",
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginLeft: "20%",
        shadowBlur: "#2224FF",
        marginTop: "5%",
        marginBottom: "5%",
        marginRight: "20%",
        borderRadius: 20,
        padding: 30,
    }

}));

function Upload(props) {
    const classes = useStyles();
    const [result, setResult] = useState(true);
    const [value, setValue] = useState([]);
    const [csvdata, setCSV] = useState('');
    const [filename, setFile] = useState('');
    const [open, setOpen] = React.useState(false);
    const handleForce = (data, fileInfo) => (setCSV(data), setFile(fileInfo));

    const papaparseOptions = {
        header: true,
        dynamicTyping: true,
        skipEmptyLines: true,
        transformHeader: header => header.toLowerCase().replace(/\W/g, "_")
    };

    async function PostCSVtoAPI() {
        // const csrf = this.getCookie('csrftoken');
        const response = await fetch('http://127.0.0.1:8000/upload/', {
            method: "POST",
            //headers: {'Content-Type': 'multipart/form-data'},
            body: JSON.stringify({
                "data": csvdata,
                "filename": filename
            })
        })
            .then((response) => response.json())
            .catch((error) => {
                setValue("maybe symptoms are not matching, Please follow the SAMPLE format");
                setOpen(true);
            });
        var obj = JSON.parse(response);
        setResult(obj["created"]);
        setValue(obj["result"]);
        setOpen(obj["open"]);
    }

    async function downloadResult() {
        setResult(true);
        fetch('http://127.0.0.1:8000/result/')
            .then(response => {
                response.blob().then(blob => {
                    let url = window.URL.createObjectURL(blob);
                    let a = document.createElement('a');
                    a.href = url;
                });
                window.location.href = response.url;
            });
    };


    return (
        <div>
            <Box display="flex" className={classes.head} flexWrap="wrap">
                {
                    (open) ? <p>open</p> : <p>Upload csv file which contains liver disease symptoms.</p>
                }
                <Box display="flex" className={classes.head} flexWrap="wrap">
                    <CSVReader
                        cssClass="react-csv-input"
                        label="Upload file"
                        onFileLoaded={handleForce}
                        parserOptions={papaparseOptions}
                        inputStyle={{opacity: 1, width: '170px', color: 'blue'}}
                    />
                    <IconLabelButtons func={downloadResult} disabled={result} label={"Result"}/>
                    <Button variant="contained" color="primary" onClick={PostCSVtoAPI}>SUBMIT</Button>

                </Box>
            </Box>
        </div>
    );
}

export default Upload;
